function filterInit(map) {
    var $filter = $('.departments__filter'),
        $items = $('.department');

    $filter.find('select').change(function(){
        var filterParams = getFilterParams($filter, $(this));

        $items.addClass('department_active');

        $items.filter(function(){
            var filterValid = true;
            for (var key in filterParams) {
                if (filterParams.hasOwnProperty(key)) {
                    if (filterParams[key].length > 0) {
                        var  filterResult = ($(this).data(key) == filterParams[key]);
                        filterValid = filterValid && filterResult;
                    }
                }
            }

            return !filterValid;

        }).removeClass('department_active');


        var marks = new ymaps.GeoObjectCollection();

        $items.filter('.department_active').each(function(){
            var latlon = $(this).data('coordinates').split(',');
            marks.add(new ymaps.Placemark(latlon));
        });


        //var bounds = new ymaps.GeoCollectionBounds(points);

        map.geoObjects.add(marks);

        map.setBounds(marks.getBounds(), {zoomMargin: 50, checkZoomRange: true});

        map.geoObjects.remove(marks);

        //map.setBounds(bounds);


    });





    //GLOBAL_arMapObjects[Object.keys(GLOBAL_arMapObjects)[0]].


    function getFilterParams($filter, $obj) {
        var filterParams = {};
        $filter.find('select').each(function(){
            var filterVal = $(this).val();
            var filterField = $(this).data('filter');
            filterParams[filterField] = filterVal;
        });

        return filterParams
    }
}
