$(function(){
    $('#regionSelect').change(function(){
       location.href = $(this).val()
    });

    $('.objects__view-switcher a').click(function(e){

        $(this).addClass('switcher__link_active').siblings()
            .each(function(){
                $(this).removeClass('switcher__link_active');
                $($(this).data('target')).hide();
            });

        $($(this).data('target')).show();

        if($(this).data('target') == '.objects__on-map')
            $('.page-nav').hide();
        else
            $('.page-nav').show();

        return false;
    });
});
